import user from '@/services/user';
import Utils from '../../utils/Cookies';

const state = {
    items: [
        { 'id': 1, 'item_name': 'Pencil', 'gl_account': 'QED-NBI-DEV-001', 'category_id': 1, 'uom': 'Each' },
    ], 
    uom: ['Each', 'Kilograms', 'Litres']
}

const getters = {
    allItems (state) {
        return state.items
    },
    itemsNumber (state) {
        return state.items.length
    },
    categoryItems: (state) => (category_id) => {
        return state.items.filter(item => item.category_id === category_id)
    },
    getItem: (state) => (item_id) => {
        return state.items.find(item => item.id === item_id)
    },
    unitofMeasures (state) {
        return state.uom
    }
}

const mutations = {
    addItem (state, item) {
        state.items.push(item)
    }
}

const actions = {
    createSupplier ({ commit }, payload) {
        commit('addItem', payload)
    }
}

export default {
    namespaced: true,
    state,
    getters,
    actions,
    mutations
}
