// import user from '@/services/user';
// import Utils from '../../utils/Cookies';

const initialState = () => ({
    activeRequisitionID: null,
    activeRequisition: {}
});

const state = {
    activeRequisitionID: null,
    activeRequisition: {
        items: [],
        approvers: [],
        status: 'In-Complete',
        id: null,
        delivery_info: {},
        requisition_by: {user:{}},
        supplier: {},
        department: {},
        request_type: {}
    }
}

const getters = {
    activeRequisitionId (state) {
        return state.activeRequisitionID
    },
    activeRequisition (state) {
        return state.activeRequisition
    },
}

const mutations = {
    updateActiveRequisitionId (state, payload) {
        state.activeRequisitionID = payload.requisitionId
    },
    updateActiveRequisition (state, payload) {
        state.activeRequisition = payload
    }
}

const actions = {
    setActiveRequisitionId ({ commit }, payload) {
        commit('updateActiveRequisitionId', payload)
    },
    setActiveRequisition ({ commit }, payload) {
        commit('updateActiveRequisition', payload)
    }
}

export default {
    namespaced: true,
    state,
    getters,
    actions,
    mutations
}
