import user from '@/services/user';
import Utils from '../../utils/Cookies';

const state = {
    suppliers: [
        { 'id': 1, 'supplier_name': 'TextBook Center', 'city': 'Nairobi', 'contact_person': 'Emmanuel', 'phone': '0795859352', 'category_id': 1 },
        { 'id': 2, 'supplier_name': 'Stationery Providers', 'city': 'Nairobi', 'contact_person': 'Emmanuel', 'phone': '0795859352', 'category_id': 1 },
        { 'id': 3, 'supplier_name': 'TextBook Center', 'city': 'Nairobi', 'contact_person': 'Emmanuel', 'phone': '0795859352', 'category_id': 2 },
        { 'id': 4, 'supplier_name': 'Kasuku Industries', 'city': 'Nairobi', 'contact_person': 'Emmanuel', 'phone': '0795859352', 'category_id': 1 }
    ], 
}

const getters = {
    allSuppliers (state) {
        return state.suppliers
    },
    suppliersNumber (state) {
        return state.suppliers.length
    },
    categorySuppliers: (state) => (category_id) => {
        return state.suppliers.filter(supplier => supplier.category_id === category_id)
    }
}

const mutations = {
    addSupplier (state, supplier) {
        state.suppliers.push(supplier)
    }
}

const actions = {
    createSupplier ({ commit }, payload) {
        commit('addSupplier', payload)
    }
}

export default {
    namespaced: true,
    state,
    getters,
    actions,
    mutations
}
