import user from '@/services/user';
import Utils from '../../utils/Cookies';

const initialState = () => ({
    glAccounts: [], 
});

// const state = initialState();

const state = {
    glAccounts: [

    ], 
    glStructure: {

    },
    currency: 'Ksh'
}

const getters = {
    glStructure (state) {
        return state.glStructure
    },
    glAccounts (state) {
        return state.glAccounts
    },
    currency (state) {
        return state.currency
    }
}

const mutations = {
    addGlAccount (state, gl_account) {
        state.glAccounts.push(gl_account)
    },
    updateGlStructure (state, payload) {
        state.glStructure = payload
    }
}

const actions = {
    createGlAccount ({ commit }, payload) {
        commit('addGlAccount', payload)
    },
    updateGlStructure ({ commit }, payload) {
        commit('updateGlStructure', payload)
    },
}

export default {
    namespaced: true,
    state,
    getters,
    actions,
    mutations
}
