import { stat } from "fs";

// import user from '@/services/user';
// import Utils from '../../utils/Cookies';

const initialState = () => ({
    budgetModule: 'active',
});

// const state = initialState();

const state = {
    budgetModule: 'active',
    rolePermissions: [
        {
            'role': 'Admin', 
            'permissions': [
                {'module': 'Requisition', 'view': true, 'create': true, 'update': true, 'delete': true, 'approve': true},
                {'module': 'PurchaseOrder', 'view': true, 'update': true, 'delete': true, 'approve': true},
                {'module': 'GoodsReceived', 'view': true, 'create': true, 'update': true, 'delete': true},
                {'module': 'Invoice', 'view': true, 'create': true, 'update': true, 'delete': true, 'approve': true},
            ]
        },
        {
            'role': 'Developer', 
            'permissions': [
                {'module': 'Requisition', 'view': true, 'create': true, 'update': true, 'delete': true, 'approve': true},
                {'module': 'PurchaseOrder', 'view': true, 'update': true, 'delete': true, 'approve': true},
                {'module': 'GoodsReceived', 'view': true, 'create': true, 'update': true, 'delete': true},
                {'module': 'Invoice', 'view': true, 'create': true, 'update': true, 'delete': true, 'approve': true},
            ]
        },
        {
            'role': 'Director', 
            'permissions': [
                {'module': 'Requisition', 'view': true, 'create': true, 'update': true, 'delete': true, 'approve': true},
                {'module': 'PurchaseOrder', 'view': true, 'update': true, 'delete': true, 'approve': true},
                {'module': 'GoodsReceived', 'view': true, 'create': true, 'update': true, 'delete': true},
                {'module': 'Invoice', 'view': true, 'create': true, 'update': true, 'delete': true, 'approve': true},
            ]
        },
        {
            'role': 'Manager', 
            'permissions': [
                {'module': 'Requisition', 'view': true, 'create': true, 'update': true, 'delete': true, 'approve': true},
                {'module': 'PurchaseOrder', 'view': true, 'update': true, 'delete': true, 'approve': true},
                {'module': 'GoodsReceived', 'view': true, 'create': true, 'update': true, 'delete': true},
                {'module': 'Invoice', 'view': true, 'create': true, 'update': true, 'delete': true, 'approve': true},
            ]
        }
    ],
    defaultCurrency: {},
    currencies: []
}

const getters = {
    budgetModuleStatus (state) {
        return state.budgetModule
    },
    companyPermissions (state) {
        return state.rolePermissions
    },
    currencies (state) {
        return state.currencies
    },
    defaultCurrency (state) {
        return state.defaultCurrency
    }
}

const mutations = {
    updateBudgetModule (state, budgetModule) {
        state.budgetModule = budgetModule
    },
    updateCompanyPermissions (state, permissionUpdate) {
        let rolePermission = state.rolePermissions.find(o => o.role === permissionUpdate.permissionUpdate.role)
        let obj = rolePermission.permissions.find(o => o.module === permissionUpdate.permissionUpdate.module)
        obj[permissionUpdate.permissionUpdate.activity] = permissionUpdate.permissionUpdate.permission
        state.rolePermissions[state.rolePermissions.findIndex(o => o.role === permissionUpdate.permissionUpdate.role)].permissions[rolePermission.permissions.findIndex(o => o.module === permissionUpdate.permissionUpdate.module)] = obj
    },
    updateCurrencies (state, currencies) {
        state.currencies = currencies
    },
    updateDefaultCurrency (state, defaultCurrency) {
        state.defaultCurrency = defaultCurrency
    },
}

const actions = {
    setBudgetModule ({ commit }, budgetModule) {
        commit('updateBudgetModule', budgetModule)
    },
    setUpdatedPermission ({ commit }, permissionUpdate) {
        commit('updateCompanyPermissions', permissionUpdate)
    },
    setCurrencies ({ commit }, currencies) {
        commit('updateCurrencies', currencies)
    },
    setDefaultCurrency ({ commit }, defaultCurrency) {
        commit('updateDefaultCurrency', defaultCurrency)
    },
}

export default {
    namespaced: true,
    state,
    getters,
    actions,
    mutations
}
