// import Utils from '../../utils/Cookies';

const initialState = () => ({
    departments: [],
});

// const state = initialState();

const state = {
    departments: [
        { 'id': 1, 'department_name': 'Finance', 'status': true },
        { 'id': 2, 'department_name': 'IT', 'status': false },
    ],
    companyUsers:[
      { 'id': 1, 'department_id': 1, 'name': 'Beatrice Njeri', 'email': 'beatrice@qedsolutions.co.ke', 'status': true, 'role': 'Manager' },
      { 'id': 2, 'department_id': 2, 'name': 'Emmanuel Gathage', 'email': 'gathage@qedsolutions.co.ke','status': true, 'role': 'Software Engineer' },
      { 'id': 3, 'department_id': 2, 'name': 'Denis Njeru', 'email': 'njerudenis@qedsolutions.co.ke', 'status': true, 'role': 'Software Engineer' },
    ]
}

const getters = {
    allDepartments (state) {
        return state.departments
    },
    departmentsNumber (state) {
        return state.departments.length
    },
    departmentUsers: (state) => (department_id) => {
        return state.companyUsers.filter(companyUser => companyUser.department_id === department_id)
    }
}

const mutations = {
    addDepartment (state, category) {
        state.departments.push(category)
    }
}

const actions = {
    createDepartment ({ commit }, payload) {
        commit('addDepartment', payload)
    }
}

export default {
    namespaced: true,
    state,
    getters,
    actions,
    mutations
}
